  `characterId` int(10) unsigned NOT NULL COMMENT '角色ID',
  `guildId` int(10) unsigned NOT NULL COMMENT '行会ID',
  `contribution` int(10) NOT NULL DEFAULT '0' COMMENT '角色行会贡献度',
