  `categories_id` int(11) DEFAULT '1',
  `steps` varchar(20) DEFAULT '' COMMENT '任务的目标列表用逗号分隔',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=112 DEFAULT CHARSET=utf8;

