/* Procedure structure for procedure `getCharacterTop` */

/*!50003 DROP PROCEDURE IF EXISTS  `getCharacterTop` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`%` PROCEDURE `getCharacterTop`(IN nickname VARCHAR(40))
BEGIN
SET @t='SET @id=0 ;';
SET @SQL = CONCAT("SELECT sw.rowno,sw.* FROM(SELECT c.nickname,c.profession,g.name,ct.battle,(@id:=@id+1) AS rowno  FROM tb_character_top AS ct LEFT JOIN tb_character AS c ON ct.characterid=c.id   LEFT JOIN tb_guild_character AS cg   ON ct.characterid=cg.characterId LEFT JOIN tb_guild AS g ON cg.guildId=g.id ORDER BY ct.battle DESC  )AS sw WHERE sw.nickname='",nickname,"' order by sw.rowno");
PREPARE sqt FROM  @t;
EXECUTE sqt;
DEALLOCATE PREPARE sqt;
PREPARE sqlstmt FROM  @SQL;
EXECUTE sqlstmt;
DEALLOCATE PREPARE sqlstmt;
END */$$
DELIMITER ;

/* Procedure structure for procedure `getTopGuild` */

/*!50003 DROP PROCEDURE IF EXISTS  `getTopGuild` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`%` PROCEDURE `getTopGuild`(IN na VARCHAR(50),IN td VARCHAR(50),IN dw VARCHAR(50))
BEGIN
SET @t='SET @id=0 ;';
SET @SQL = CONCAT("SELECT sw.rowno,sw.* FROM(SELECT g.id,g.name,g.memberCount,gt.technologyLevel,",na,",(@id:=@id+1) AS rowno FROM tb_guild AS g LEFT JOIN tb_guild_technology AS gt ON g.id=gt.guildId   ORDER BY  ",td," )AS sw WHERE sw.name='",dw,"' order by sw.rowno");
PREPARE sqt FROM  @t;
EXECUTE sqt;
DEALLOCATE PREPARE sqt;
PREPARE sqlstmt FROM  @SQL;
EXECUTE sqlstmt;
DEALLOCATE PREPARE sqlstmt;
END */$$
DELIMITER ;

