set @t='SET @rowno=0 ;';
SET @SQL = CONCAT("SELECT rowno,sw.* FROM(SELECT tit.name,c.nickname,c.profession,t.marks,t.itemid,(@rowno:=@rowno+1)AS rowno FROM tb_topitem AS t LEFT JOIN tb_item AS i ON t.itemid=i.id LEFT JOIN tb_character AS c ON i.characterId=c.id JOIN tb_item_template AS tit ON i.itemTemplateId=tit.id  WHERE t.typeid=",typeid," ORDER BY t.marks,i.strengthen)AS sw WHERE sw.nickname='",nickname,"' order by sw.rowno");
